import styled from 'styled-components'

export const TitleSection = styled.h2 `
	font-weight: 700;
  font-size: calc((100vw - 320px) / (1600 - 320) * (48 - 28) + 28px);
  line-height: 0.5;
  text-align: center;
  color: #ccc;
  margin-bottom: 50px;
`
