import React from 'react'
import styled from 'styled-components'
import Photo from '../../../assets/img/photo/photo.jpg'
import { FlexWrapper } from '../../../components/styles-components/flexwrapper/FlexWrapper'
import { theme } from '../../../styles/Theme'

export const Hero = () => {
	return (
		<SectionHero>
			<FlexWrapper justify={'space-between'} align={'center'}>
				<div>
					<Title>Hi ðŸ‘‹ , <br></br>
						My name is <br></br>
						<ColorText>Alex Ovsiannikov</ColorText> <br></br>
						I build things for web</Title>
				</div>
				<PhotoImg src={Photo} alt={'Photo Alex Ovsiannikov'} />
			</FlexWrapper>
		</SectionHero>
	)
}

const SectionHero = styled.section `
	min-height: 100vh;
	display: flex;
	justify-content: center;
	flex-direction: column;
`

const Title = styled.h1 `
  font-weight: 700;
  font-size: 58px;
  line-height: 1.2069;
  letter-spacing: -0.02em;
  color: #d9d9d9;
`


const ColorText = styled.span `
  background: linear-gradient(90deg, #13b0f5 2.6%, #e70faa 100%);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`

const PhotoImg = styled.img`
	position: relative;
  width: 100%;
  max-width: 350px;
  aspect-ratio: 1/1;
  object-fit: cover;
  border-radius: 230px;
	z-index: 999;

	&::before {
		position: absolute;
		content: ' dsadsadas';
		top: 0;
		left: 0;
		width: 360px;
		height: 360px;
		border-radius: 230px;
		color: cadetblue;
		//background: linear-gradient(90deg, #13b0f5 2.6%, #e70faa 100%);
		background-color: cadetblue;
		z-index: -1;
	}
`
