import React from 'react'
import styled from 'styled-components'
import { Icon } from '../../../../components/icon/Icon'
import { FlexWrapper } from '../../../../components/styles-components/flexwrapper/FlexWrapper'
import { font } from '../../../../styles/Common'
import { theme } from '../../../../styles/Theme'

type  WorkExperiencePropsType = {
	position: string,
	location: string,
	city: string,
	time: string,
	date: string
}

export const Experience = (props: WorkExperiencePropsType) => {
	return (
		<ExperienceWrapper>
			<div>
				<ExperiencePosition>{props.position}</ExperiencePosition>
				<FlexWrapper gap={'40px'}>
					<ExperienceText><Icon iconId={'home'} width={'7px'} height={'9px'} viewBox={'0 0 7 9'} />{props.location}
					</ExperienceText>
					<ExperienceText><Icon iconId={'pin'} width={'12px'} height={'12px'} viewBox={'0 0 12 12'} />{props.city}
					</ExperienceText>
				</FlexWrapper>
			</div>

			<FlexWrapper direction={'column'} align={'flex-end'} gap={'10px'} wrap={'wrap'}>
				<ExperienceTime>{props.time}</ExperienceTime>
				<ExperienceText><Icon iconId={'date'} width={'12px'} height={'10px'} viewBox={'0 0 12 10'} />{props.date}
				</ExperienceText>
			</FlexWrapper>
		</ExperienceWrapper>
	)
}

const ExperienceWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  gap: 20px;
	flex-wrap: wrap;
  padding-bottom: 25px;
  margin-bottom: 30px;
  border-bottom: 2px solid ${theme.colors.subTitle};
`

const ExperiencePosition = styled.p `
  ${font({ weight: 400, lineHeight: 1.4, color: theme.colors.font, Fmax: 20, Fmin: 14 })};

  margin-bottom: 10px;
	letter-spacing: 0.05em;
`

const ExperienceText = styled.p`
  ${font({ weight: 500, lineHeight: 2.3, color: theme.colors.subTitle, Fmax: 12, Fmin: 10 })};

  display: flex;
  align-items: center;
  gap: 8px;
  justify-content: space-between;
`

const ExperienceTime = styled.p `
	${font({weight:600,lineHeight:2.8,color:theme.colors.primaryBg,Fmax:9,Fmin:7})};

	height: 24px;
	padding: 1px 20px;
	border-radius: 100px;
	background-color: ${theme.colors.font};
`
